%% Fit: 'fit4'.
[xData, yData] = prepareCurveData( distancesFiltered, readingsFiltered );

% Set up fittype and options.
ft = fittype( 'power1' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [8224.773209451 -1.11297155414937];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

