% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[10.1556420233463 90.1556420233463]);
box(axes1,'on');
grid(axes1,'on');
hold(axes1,'off');
% Create legend
legend(axes1,'show');

